import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Notes.css';

function Notes() {
  const [notes, setNotes] = useState([]);
  const [title, setTitle] = useState('');
  const [content, setContent] = useState('');
  const [editNoteId, setEditNoteId] = useState(null);

  // Logout Handler
  const handleLogout = () => {
    localStorage.removeItem("token");
    sessionStorage.clear();
    window.location.href = "/";
  };

  // Fetch notes from backend
  const fetchNotes = async () => {
    try {
      const token = localStorage.getItem('token');
      const res = await axios.get('http://localhost:5000/api/notes', {
        headers: { Authorization: `Bearer ${token}` }
      });

      if (res.data.success) {
        setNotes(res.data.notes);
      }
    } catch (error) {
      console.error("Error fetching notes:", error);
    }
  };

  useEffect(() => {
    fetchNotes();
  }, []);

  // Reset input fields
  const resetForm = () => {
    setTitle('');
    setContent('');
    setEditNoteId(null);
  };

  // Save or update note
  const handleSubmit = async (e) => {
    e.preventDefault();
    const token = localStorage.getItem('token');

    if (!title.trim() && !content.trim()) return;

    try {
      const payload = { title, content };

      if (editNoteId) {
        // Edit mode
        await axios.put(
          `http://localhost:5000/api/notes/${editNoteId}`,
          payload,
          { headers: { Authorization: `Bearer ${token}` } }
        );
      } else {
        // Add new note
        await axios.post(
          'http://localhost:5000/api/notes',
          payload,
          { headers: { Authorization: `Bearer ${token}` } }
        );
      }

      resetForm();
      fetchNotes();
    } catch (error) {
      console.error("Error saving note:", error);
    }
  };

  // Delete note
  const handleDelete = async (id) => {
    try {
      const token = localStorage.getItem('token');
      await axios.delete(`http://localhost:5000/api/notes/${id}`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      fetchNotes();
    } catch (error) {
      console.error("Error deleting note:", error);
    }
  };

  // Prepare note for editing
  const handleEdit = (note) => {
    setTitle(note.title);
    setContent(note.content);
    setEditNoteId(note._id);
  };

  return (
    <div className="main">
      <header className="header">
        <div className="navbar">
          <h2 className="logo">Keeper</h2>
          <div className="nav-texts">
            <a className="nav-item" href="/friends">Friends</a>
            <a className="nav-item">Notifications</a>
            <a className="nav-item" onClick={handleLogout}>Logout</a>
          </div>
        </div>
      </header>

      {/* Note Form */}
      <form className="create-note" onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="Title"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
        />
        <textarea
          placeholder="Take a note..."
          value={content}
          onChange={(e) => setContent(e.target.value)}
          rows="3"
        />
        <div>
          <button className="add-btn" type="submit">
            {editNoteId ? 'Update' : 'Add'}
          </button>
          {editNoteId && (
            <button
              type="button"
              className="cancel-btn"
              onClick={resetForm}
            >
              Cancel
            </button>
          )}
        </div>
      </form>

      {/* Notes List */}
      <div className="notes-list">
        {notes.length === 0 ? (
          <p style={{ textAlign: 'center' }}>No notes yet.</p>
        ) : (
          notes.map(note => (
            <div className="note" key={note._id}>
              <h1>{note.title}</h1>
              <p>{note.content}</p>
              <div className="note-actions">
                <button className="edit-btn" onClick={() => handleEdit(note)}>Edit</button>
                <button className="delete-btn" onClick={() => handleDelete(note._id)}>Delete</button>
              </div>
            </div>
          ))
        )}
      </div>
    </div>
  );
}

export default Notes;
