import React, { useEffect, useState } from "react";
import axios from "axios";

const FriendsTab = () => {
  const [users, setUsers] = useState([]);
  const [friendRequests, setFriendRequests] = useState([]);

  const currentUserId = localStorage.getItem("userId"); // or from JWT

  useEffect(() => {
    axios.get("/api/users") // API to fetch all users
      .then((res) => {
        const filtered = res.data.filter(u => u._id !== currentUserId);
        setUsers(filtered);
      })
      .catch((err) => console.error(err));
  }, []);

  const sendFriendRequest = (toUserId) => {
    axios.post("/api/friend-request", {
      from: currentUserId,
      to: toUserId
    })
    .then(() => {
      setFriendRequests([...friendRequests, toUserId]);
    })
    .catch(err => console.error(err));
  };

  return (
    <div className="friends-tab">
      <h2>People You Might Know</h2>
      <ul>
        {users.map((user) => (
          <li key={user._id} className="user-card">
            <span>{user.name}</span>
            {friendRequests.includes(user._id) ? (
              <button disabled>Request Sent</button>
            ) : (
              <button onClick={() => sendFriendRequest(user._id)}>Send Request</button>
            )}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default FriendsTab;
