import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Notes.css';

const handleLogout = () => {
  // Example: Clear token/localStorage
  localStorage.removeItem("token"); // or whatever key you're using
  sessionStorage.clear(); // optional
  // Redirect to login page
  window.location.href = "/"; // change if your route is different
};

function Notes() {
  const [title, setTitle] = useState('');
  const [content, setContent] = useState('');
  const [notes, setNotes] = useState([]);

  const fetchNotes = async () => {
    try {
      const token = localStorage.getItem('token');
      const res = await axios.get('http://localhost:5000/api/notes', {
        headers: { Authorization: `Bearer ${token}` }
      });
      if (res.data.success) {
        setNotes(res.data.notes);
      }
    } catch (error) {
      console.error(error);
    }
  };

  useEffect(() => {
    fetchNotes();
  }, []);

  const handleSaveNote = async (e) => {
    e.preventDefault();
    try {
      const token = localStorage.getItem('token');
      const res = await axios.post(
        'http://localhost:5000/api/notes',
        { title, content },
        { headers: { Authorization: `Bearer ${token}` } }
      );

      if (res.data.success) {
        setTitle('');
        setContent('');
        fetchNotes();
      }
    } catch (error) {
      console.error(error);
    }
  };

  const handleDeleteNote = async (noteId) => {
    try {
      const token = localStorage.getItem('token');
      const res = await axios.delete(`http://localhost:5000/api/notes/${noteId}`, {
        headers: { Authorization: `Bearer ${token}` }
      });

      if (res.data.success) {
        fetchNotes();
      }
    } catch (error) {
      console.error(error);
    }
  };
   const handleEditNote = (note) => {
    setEditMode(true);
    setEditNoteId(note._id);
    setTitle(note.title);
    setContent(note.content);
  };

  const resetForm = () => {
    setTitle('');
    setContent('');
    setEditMode(false);
    setEditNoteId(null);
  };

  return (
    <div className="main">
      <header className="header">
        <div className="navbar">
          <h2 className="logo">Keeper</h2>
          <div className="nav-texts">
            <a className="nav-item" href="/friends">Friends</a>
            <a className="nav-item">Notifications</a>
            <a className="nav-item" onClick={handleLogout}>Logout</a>
          </div>
        </div>
      </header>

      <form className="create-note" onSubmit={handleSaveNote}>
        <input
          type="text"
          placeholder="Title"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
        />
        <textarea
          placeholder="Take a note..."
          value={content}
          onChange={(e) => setContent(e.target.value)}
          rows="3"
        />
        <button className="add-btn" type="submit">+</button>
      </form>

      <div className="notes-list">
        {notes.map(note => (
          <div className="note" key={note._id}>
            <h1>{note.title}</h1>
            <p>{note.content}</p>
            <button className="edit-btn" onClick={() => handleEditNote(note)}>EDIT</button>
            <button className="delete-btn" onClick={() => handleDeleteNote(note._id)}>DELETE</button>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Notes;
