import React, { useState } from 'react';
import './login.css';

function Login() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  const handleLogin = (e) => {
    e.preventDefault();
    console.log('Email:', email);
    console.log('Password:', password);
  };

  return (
    <div className="inkspire-container">
      {/* Left Section */}
      <div className="inkspire-left">
        <h1 className="logo">Inkspire</h1>
        <p>Capture ideas. Spark creativity.</p>
      </div>

      {/* Right Section */}
      <div className="inkspire-right">
        <form className="login-form" onSubmit={handleLogin}>
          <input
            type="email"
            placeholder="Email address"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
          <input
            type="password"
            placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
          <button type="submit" className="login-btn">Log In</button>
          <a href="#" className="forgot-link">Forgot Password?</a>
          <hr />
          <button type="button" className="create-btn">Create New Account</button>
        </form>
      </div>
    </div>
  );
}

export default Login;
