import React, { useState, useEffect } from 'react';
import axios from 'axios';

function Notes() {
  const [title, setTitle] = useState('');
  const [content, setContent] = useState('');
  const [notes, setNotes] = useState([]);
  const [showForm, setShowForm] = useState(false);

  const fetchNotes = async () => {
    try {
      const token = localStorage.getItem('token');
      const res = await axios.get('http://localhost:5000/api/notes', {
        headers: { Authorization: `Bearer ${token}` }
      });
      if (res.data.success) {
        setNotes(res.data.notes);
      }
    } catch (error) {
      console.error('Error fetching notes:', error);
    }
  };

  useEffect(() => {
    fetchNotes();
  }, []);

  const handleSaveNote = async (e) => {
    e.preventDefault();
    try {
      const token = localStorage.getItem('token');
      const res = await axios.post(
        'http://localhost:5000/api/notes',
        { title, content },
        { headers: { Authorization: `Bearer ${token}` } }
      );

      if (res.data.success) {
        alert('‚úÖ Note saved successfully!');
        setTitle('');
        setContent('');
        setShowForm(false);
        fetchNotes();
      } else {
        alert(res.data.message);
      }
    } catch (error) {
      console.error('Error saving note:', error);
      alert('‚ùå Error saving note');
    }
  };

  return (
    <div style={{ maxWidth: '800px', margin: '50px auto', padding: '20px' }}>
      <h2 style={{ textAlign: 'center' }}>üìù My Notes</h2>

      <div style={{ textAlign: 'center', marginBottom: '20px' }}>
        <button
          onClick={() => setShowForm(!showForm)}
          style={{
            padding: '10px 20px',
            backgroundColor: showForm ? '#dc3545' : '#007bff',
            color: '#fff',
            border: 'none',
            borderRadius: '5px',
            cursor: 'pointer'
          }}
        >
          {showForm ? 'Close' : '‚ûï Add Note'}
        </button>
      </div>

      {showForm && (
        <form onSubmit={handleSaveNote} style={{ marginBottom: '30px' }}>
          <input
            type="text"
            placeholder="Title"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            required
            style={{
              width: '100%',
              padding: '10px',
              marginBottom: '10px',
              borderRadius: '4px',
              border: '1px solid #ccc'
            }}
          />
          <textarea
            placeholder="Write your note..."
            value={content}
            onChange={(e) => setContent(e.target.value)}
            required
            rows="5"
            style={{
              width: '100%',
              padding: '10px',
              marginBottom: '10px',
              borderRadius: '4px',
              border: '1px solid #ccc'
            }}
          ></textarea>
          <button
            type="submit"
            style={{
              padding: '10px 20px',
              backgroundColor: '#28a745',
              color: '#fff',
              border: 'none',
              borderRadius: '5px',
              cursor: 'pointer'
            }}
          >
            üíæ Save Note
          </button>
        </form>
      )}

      {notes.length === 0 ? (
        <p style={{ textAlign: 'center', color: '#888' }}>No notes yet</p>
      ) : (
        <table
          style={{
            width: '100%',
            borderCollapse: 'collapse',
            border: '1px solid #ccc'
          }}
        >
          <thead>
            <tr style={{ backgroundColor: '#f8f9fa' }}>
              <th style={thStyle}>Title</th>
              <th style={thStyle}>Content</th>
              <th style={thStyle}>Actions</th>
            </tr>
          </thead>
          <tbody>
            {notes.map((note) => (
              <tr key={note._id}>
                <td style={tdStyle}>{note.title}</td>
                <td style={tdStyle}>{note.content}</td>
                <td style={tdStyle}>
                  <button style={actionBtnStyle}>‚úèÔ∏è Edit</button>
                  <button style={{ ...actionBtnStyle, backgroundColor: '#dc3545' }}>
                    üóëÔ∏è Delete
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}

// Styles
const thStyle = {
  padding: '10px',
  borderBottom: '1px solid #ccc',
  textAlign: 'left'
};

const tdStyle = {
  padding: '10px',
  borderBottom: '1px solid #eee'
};

const actionBtnStyle = {
  marginRight: '10px',
  padding: '5px 10px',
  backgroundColor: '#17a2b8',
  color: '#fff',
  border: 'none',
  borderRadius: '3px',
  cursor: 'pointer'
};

export default Notes;
