import React, { useEffect, useState } from "react";
import axios from "axios";
import './login.css';
import './FriendsTab.css';

function FriendsTab() {
  const [users, setUsers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [sentRequests, setSentRequests] = useState([]); // Track sent requests

  // ...existing code...

  return (
    <div>
      <div className="header1">
        <img src="/favicon.png" alt="Logo" className="logo" />
        <h2 className="title">Friends Tab</h2>
      </div>
      <div className="friends-tab">
          <h3>People you might know</h3>
          {users.length === 0 ? (
            <p>No other users found.</p>
          ) : (
            <div className="friends-list">
              {users.map((user) => (
                <div className="friend-card" key={user._id}>
                  <div className="friend-info">
                    <p className="friend-name">{user.fname} {user.lname}</p>
                  </div>
                  <button
                    className="send-request-btn"
                    disabled={sentRequests.includes(user._id)}
                    onClick={async () => {
                          try {
                            await axios.post("http://localhost:5000/api/friend-request", {
                              from: currentUserId,
                              to: user._id
                            });
                            setSentRequests(prev => [...prev, user._id]); // Mark as sent
                            alert(`Friend request sent to ${user.fname} ${user.lname}`);
                          } catch (error) {
                            console.log(error);
                            alert(error.response?.data?.message || "Failed to send request");
                          }
                        }}
                  >
                    {sentRequests.includes(user._id) ? "Request Sent" : "Add Friend"}
                  </button>
                </div>
              ))}
            </div>
          )}
        </div>
    </div>
  );
}

export default FriendsTab;
