import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Notes.css';

function Notes() {
  const [title, setTitle] = useState('');
  const [content, setContent] = useState('');
  const [notes, setNotes] = useState([]);
  const [showForm, setShowForm] = useState(false);

  const fetchNotes = async () => {
    try {
      const token = localStorage.getItem('token');
      const res = await axios.get('http://localhost:5000/api/notes', {
        headers: { Authorization: `Bearer ${token}` }
      });
      if (res.data.success) {
        setNotes(res.data.notes);
      }
    } catch (error) {
      console.error(error);
    }
  };

  useEffect(() => {
    fetchNotes();
  }, []);

  const handleSaveNote = async (e) => {
    e.preventDefault();
    try {
      const token = localStorage.getItem('token');
      const res = await axios.post(
        'http://localhost:5000/api/notes',
        { title, content },
        { headers: { Authorization: `Bearer ${token}` } }
      );

      if (res.data.success) {
        setTitle('');
        setContent('');
        setShowForm(false);
        fetchNotes(); // Refresh notes list
      } else {
        alert(res.data.message);
      }
    } catch (error) {
      console.error(error);
      alert('Error saving note');
    }
  };

  // üí• NEW FUNCTION TO DELETE NOTE
  const handleDeleteNote = async (noteId) => {
    try {
      const token = localStorage.getItem('token');
      const res = await axios.delete(`http://localhost:5000/api/notes/${noteId}`, {
        headers: { Authorization: `Bearer ${token}` }
      });

      if (res.data.success) {
        fetchNotes(); // Refresh notes list after delete
      } else {
        alert('Failed to delete note');
      }
    } catch (error) {
      console.error(error);
      alert('Error deleting note');
    }
  };

  return (
    <div className="notes-container">
      <h1 className="heading">üìù My Notes</h1>

      <button className="toggle-btn" onClick={() => setShowForm(!showForm)}>
        {showForm ? 'Close' : 'Create Note'}
      </button>

      {showForm && (
        <form onSubmit={handleSaveNote} className="note-form">
          <input
            type="text"
            placeholder="Title"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            required
          />
          <textarea
            placeholder="Write your note..."
            value={content}
            onChange={(e) => setContent(e.target.value)}
            required
            rows="4"
          ></textarea>
          <button type="submit">Save</button>
        </form>
      )}

      <div className="notes-grid">
        {notes.map(note => (
          <div className="note-card" key={note._id}>
            <h3>{note.title}</h3>
            <p>{note.content}</p>
            {/* üóëÔ∏è DELETE BUTTON */}
            <button className="delete-btn" onClick={() => handleDeleteNote(note._id)}>
              Delete
            </button>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Notes;
