import React, { useEffect, useState } from "react";
import axios from "axios";

const FriendsTab = () => {
  const [users, setUsers] = useState([]);
  const [friendRequests, setFriendRequests] = useState([]);

  const currentUserId = localStorage.getItem("userId"); // logged in user ID

  useEffect(() => {
    axios.get("/api/users")
      .then((res) => {
        const filtered = res.data.filter(user => user._id !== currentUserId);
        setUsers(filtered);
      })
      .catch((err) => console.error("Error fetching users:", err));
  }, [currentUserId]);

  const sendFriendRequest = async (toUserId) => {
    try {
      await axios.post("/api/friend-request", {
        from: currentUserId,
        to: toUserId,
      });
      setFriendRequests((prev) => [...prev, toUserId]);
    } catch (err) {
      console.error("Error sending friend request:", err);
    }
  };

  return (
    <div className="friends-tab">
      <h2>People You Might Know</h2>
      <ul>
        {users.map((user) => (
          <li key={user._id} className="user-card">
            <span>{user.fname} {user.lname}</span>
            {friendRequests.includes(user._id) ? (
              <button disabled>Request Sent</button>
            ) : (
              <button onClick={() => sendFriendRequest(user._id)}>Send Request</button>
            )}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default FriendsTab;
