import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import './login.css';

function Login() {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [loggedInUser, setLoggedInUser] = useState(null); // For storing user details

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post('http://localhost:5000/api/auth/login', {
        username,
        password
      });

      console.log();


      if (res.data.success) {
        setLoggedInUser(res.data.user); // Store user details
        console.log('Login Successful:', res.data.user);
      } else {
        alert(res.data.message);
      }
    } catch (error) {
      console.error('Login Error:', error);
      alert('Invalid credentials');
    }
  };

  return (
    <div className="login-wrapper">
      <div className="login-box">
        {!loggedInUser ? (
          <>
            <h2>SIGN IN</h2>
            <form onSubmit={handleLogin}>
              <div className="input-group">
                <input
                  type="text"
                  value={username}
                  onChange={(e) => setUsername(e.target.value)}
                  required
                />
                <label className={username ? 'active' : ''}>Username</label>
              </div>
              <div className="input-group">
                <input
                  type="password"
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                  required
                />
                <label className={password ? 'active' : ''}>Password</label>
              </div>
              <div className="options">
                <label>
                  <input type="checkbox" /> Remember me
                </label>
                <a href="#">Forgot Password?</a>
              </div>
              <button type="submit" className="login-btn">Login</button>
            </form>
            <p style={{ marginTop: '15px' }}>
              Donâ€™t have an account? <Link to="/signup" className="signup-link">Create New Account</Link>
            </p>
          </>
        ) : (
          <h2>Hello {loggedInUser.fname} {loggedInUser.lname}</h2>
        )}
      </div>
    </div>
  );
}

export default Login;
