import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Notes.css';

const handleLogout = () => {
  localStorage.removeItem("token");
  sessionStorage.clear();
  window.location.href = "/";
};

function Notes() {
  const [title, setTitle] = useState('');
  const [content, setContent] = useState('');
  const [notes, setNotes] = useState([]);
  const [editMode, setEditMode] = useState(false);
  const [editNoteId, setEditNoteId] = useState(null);

  const fetchNotes = async () => {
    try {
      const token = localStorage.getItem('token');
      const res = await axios.get('http://localhost:5000/api/notes', {
        headers: { Authorization: `Bearer ${token}` }
      });
      if (res.data.success) {
        setNotes(res.data.notes);
      }
    } catch (error) {
      console.error(error);
    }
  };

  useEffect(() => {
    fetchNotes();
  }, []);

  const handleSaveNote = async (e) => {
    e.preventDefault();
    const token = localStorage.getItem('token');

    try {
      if (editMode) {
        // Edit mode: PUT request
        const res = await axios.put(
          `http://localhost:5000/api/notes/${editNoteId}`,
          { title, content },
          { headers: { Authorization: `Bearer ${token}` } }
        );
        if (res.data.success) {
          resetForm();
          fetchNotes();
        }
      } else {
        // Normal mode: POST request
        const res = await axios.post(
          'http://localhost:5000/api/notes',
          { title, content },
          { headers: { Authorization: `Bearer ${token}` } }
        );
        if (res.data.success) {
          resetForm();
          fetchNotes();
        }
      }
    } catch (error) {
      console.error(error);
    }
  };

  const handleDeleteNote = async (noteId) => {
    try {
      const token = localStorage.getItem('token');
      const res = await axios.delete(`http://localhost:5000/api/notes/${noteId}`, {
        headers: { Authorization: `Bearer ${token}` }
      });

      if (res.data.success) {
        fetchNotes();
      }
    } catch (error) {
      console.error(error);
    }
  };

  const handleEditNote = (note) => {
    setEditMode(true);
    setEditNoteId(note._id);
    setTitle(note.title);
    setContent(note.content);
  };

  const resetForm = () => {
    setTitle('');
    setContent('');
    setEditMode(false);
    setEditNoteId(null);
  };

  return (
    <div className="main">
      <header className="header">
        <div className="navbar">
          <h2 className="logo">Keeper</h2>
          <div className="nav-texts">
            <a className="nav-item" href="/friends">Friends</a>
            <a className="nav-item">Notifications</a>
            <a className="nav-item" onClick={handleLogout}>Logout</a>
          </div>
        </div>
      </header>

      <form className="create-note" onSubmit={handleSaveNote}>
        <input
          type="text"
          placeholder="Title"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
        />
        <textarea
          placeholder="Take a note..."
          value={content}
          onChange={(e) => setContent(e.target.value)}
          rows="3"
        />
        <div>
          <button className="add-btn" type="submit">
            {editMode ? "Update" : "+"}
          </button>
          {editMode && (
            <button type="button" onClick={resetForm} className="cancel-btn">
              Cancel
            </button>
          )}
        </div>
      </form>

      <div className="notes-list">
        {notes.map(note => (
          <div className="note" key={note._id}>
            <h1>{note.title}</h1>
            <p>{note.content}</p>
            <div className="note-actions">
              <button className="edit-btn" onClick={() => handleEditNote(note)}>EDIT</button>
              <button className="delete-btn" onClick={() => handleDeleteNote(note._id)}>DELETE</button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Notes;
